import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Home from './pages/Home';
import AddBook from './pages/AddBook';
import EditBook from './pages/EditBook';
import AddUser from './pages/AddUser';
import EditUser from './pages/EditUser';
import AddBorrowing from './pages/AddBorrowing';
import EditBorrowing from './pages/EditBorrowing';

function App() {
  return (
    <Router>
      <div className="container mx-auto p-4">
        <nav className="mb-4">
          <Link to="/" className="mr-4">Home</Link>
          <Link to="/add-book" className="mr-4">Add Book</Link>
          <Link to="/add-user" className="mr-4">Add User</Link>
          <Link to="/add-borrowing" className="mr-4">Add Borrowing</Link>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/add-book" element={<AddBook />} />
          <Route path="/edit-book/:id" element={<EditBook />} />
          <Route path="/add-user" element={<AddUser />} />
          <Route path="/edit-user/:id" element={<EditUser />} />
          <Route path="/add-borrowing" element={<AddBorrowing />} />
          <Route path="/edit-borrowing/:id" element={<EditBorrowing />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
